package operators;

public class ArithmeticOperatorsAddition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 20;//addition through arithmetic operators
		System.out.println("a=10");//to print the value with "" like of a string (method)
		System.out.println("b=20");
		int c = a + b;//to add values or operands
		System.out.println("c= a+b  \n"+ c);//"\n" new line
	}

}
